mapper.select:

	select *
	from
	(
	    select rownum as RNUM, blist.*
	    from(
	          select * from question_view order by no desc		        
	    ) blist
	) 
	
	where RNUM > #{firstRecordIndex} 
	and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
update question
set reply_flag='Y'
where no=#{no}
