mapper.update:
update member
set mem_pwd=#{memPwd}
where mem_id=#{memId}

mapper.select:
select *
from
(
    select rownum as RNUM, blist.*
    from(
       select * from hire_noti_view
        
       order by scrap_no desc		        
    ) blist
) 

where RNUM > #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}
