mapper.select:
select *
from (
    select * from reboard order by no desc
)	

where rownum <= 5

mapper.update:
update reboard
set readcount=readcount+1 
where no=#{no}

update reboard
set title=#{title},content=#{content}
where no=#{no}

mapper.select:
select *
from
(
    select rownum as RNUM, blist.*
    from(
        select * from reboard 
        
        order by no desc		        
    ) blist
) 

where RNUM > #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}
